             player[msg.sender].goldCoin-=amount;
             Inventory[msg.sender].healthPotion+=num1;
             Inventory[msg.sender].qiRecoveryPill+=num2;
             Inventory[msg.sender].attackEnhancePotion+=num3;
             Inventory[msg.sender].goldDropEnhancePotion+=num4;
    }

